// STEP 1: Get and clean the new link from RSS
const newLinkRaw = $input.item.json.link?.trim();
if (!newLinkRaw) {
  return [{ json: { check: "false", error: "No link found" } }];
}

// STEP 2: Normalize URL for comparison
function cleanUrl(url) {
  try {
    const u = new URL(url.trim());
    return (u.origin + u.pathname).toLowerCase().replace(/\/+$/, "");
  } catch (err) {
    return url.trim().toLowerCase().replace(/\/+$/, "");
  }
}
const newLink = cleanUrl(newLinkRaw);

// STEP 3: Get ALL existing links from sheet (input 1)
// $input.all() returns [ new_item, sheet_row_1, sheet_row_2, ... ]
// So skip the first item (it's the new one), and get links from the rest
const existingLinks = $input.all()
  .slice(1) // all items from input 1 (sheet data)
  .map(item => {
    const link = item.json?.link || item.link;
    return link ? cleanUrl(link) : null;
  })
  .filter(Boolean);

// STEP 4: Check for duplicate
const isDuplicate = existingLinks.includes(newLink);

// STEP 5: Output result
return [{
  json: {
    Title: $input.item.json.title,
    Link: newLinkRaw,
    Author: $input.item.json.author,
    PubDate: $input.item.json.pubDate,
    Summary: $input.item.json.summary,
    check: isDuplicate ? "true" : "false"
  },
  binary: $input.item.binary
}];

